  @override
  Widget build(BuildContext context) {
    // 将食谱按分类分组
    Map<String, List<Map<String, String>>> groupedContent = {};
    contentList.forEach((recipe) {
      String category = recipe['category']!;
      if (!groupedContent.containsKey(category)) {
        groupedContent[category] = [];
      }
      groupedContent[category]!.add(recipe);
    });

    return Scaffold(
      appBar: AppBar(
        title: const Text('药膳食谱'),
        backgroundColor: Colors.purple[50], // 设置背景颜色为浅紫色
      ),
      body: ListView.builder(
        itemCount: groupedContent.length,
        itemBuilder: (context, index) {
          String category = groupedContent.keys.elementAt(index);
          List<Map<String, String>> recipes = groupedContent[category]!;
          return Column(
            crossAxisAlignment: CrossAxisAlignment.start,
            children: [
              Padding(
                padding: const EdgeInsets.all(8.0),
                child: Text(
                  category,
                  style: TextStyle(
                    fontWeight: FontWeight.bold,
                    fontSize: 18,
                  ),
                ),
              ),
              ListView.builder(
                shrinkWrap: true,
                physics: ClampingScrollPhysics(),
                itemCount: recipes.length,
                itemBuilder: (context, index) {
                  return GestureDetector(
                    onTap: () {
                      Navigator.push(
                        context,
                        MaterialPageRoute(
                          builder: (context) => RecipeDetailPage(content: recipes[index]),
                        ),
                      );
                    },
                    child: Card(
                      margin: EdgeInsets.all(8),
                      child: Padding(
                        padding: const EdgeInsets.all(8.0),
                        child: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(
                              '疾病名称：${recipes[index]['name']}',
                              style: TextStyle(
                              ),
                            ),
                            SizedBox(height: 8),
                            Text(
                              '点击查看详情',
                              style: TextStyle(
                                color: Colors.blue,
                                decoration: TextDecoration.underline,
                              ),
                            ),
                          ],
                        ),
                      ),
                    ),
                  );
                },
              ),
            ],
          );
        },
      ),
    );
  }
}

class RecipeDetailPage extends StatelessWidget {
  final Map<String, String> content;

  const RecipeDetailPage({Key? key, required this.content}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(content['name']!),
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: SingleChildScrollView(
          child: Text(
            content['detailed_content']!,
            style: TextStyle(fontSize: 16),
          ),
        ),
      ),
    );
  }
}

void main() {
  runApp(MaterialApp(
    home: YaoShanShiPuPage(),
  ));
}
